mapper.select:
SELECT rnum, r.recipe_board_id, m.member_nick, r.recipe_board_title, m.member_image, r.recipe_board_image, r.recipe_board_hits FROM ( SELECT ROW_NUMBER() OVER(ORDER BY
recipe_reg_date DESC
COALESCE(like_count, 0) DESC, recipe_reg_date DESC
recipe_board_hits DESC, recipe_reg_date DESC
) AS rnum,
rb.recipe_board_id, rb.member_email, rb.recipe_board_title, rb.recipe_board_image, rb.recipe_board_hits FROM recipe_board rb
COALESCE(lr.like_count, 0) AS like_count, rb.recipe_board_id, rb.member_email, rb.recipe_board_title, rb.recipe_board_image, rb.recipe_board_hits FROM recipe_board rb LEFT JOIN ( SELECT recipe_board_id, COUNT(recipe_board_id) AS like_count FROM like_rkh GROUP BY recipe_board_id ) lr ON rb.recipe_board_id = lr.recipe_board_id
category_type = #{type}
and category_ingredients = #{ingredients}
and category_method = #{method}
and recipe_board_title like '%' || #{searchValue} || '%'
) r INNER JOIN member m ON r.member_email = m.member_email AND rnum BETWEEN #{pagination.startRowNumber} AND #{pagination.endRowNumber} ORDER BY rnum

mapper.update:
update recipe_board set recipe_board_title = #{recipeBoardTitle},
recipe_board_content=#{recipeBoardContent},
recipe_edit_date=sysdate,
category_type = #{categoryType}, category_ingredients =
#{categoryIngredients} , category_method = #{categoryMethod}

	, recipe_board_image=#{recipeBoardImage}

where recipe_board_id = #{recipeBoardId}

mapper.select:
select count(*) from recipe_board


		category_type = #{type}


		and category_ingredients = #{ingredients}


		and category_method = #{method}


		and recipe_board_title like '%' || #{searchValue} || '%'
